let startScreen=document.querySelector(".startScreen"),descriptionScreen=document.querySelector(".descriptionScreen"),nextBtns=document.getElementsByClassName("nextBtn");for(let e=0;e<nextBtns.length;e++){const t=nextBtns[e],n=t.closest("section");t.addEventListener("click",(()=>{n.classList.add("hidd"),n.nextElementSibling.classList.remove("hidd")}))}function hidePreloader(){setTimeout((()=>{startScreen.classList.add("hidd"),descriptionScreen.classList.remove("hidd")}),1e3)}hidePreloader();